export default class MessageUtils {
  static onSuccessMessage(message) {
    return {
      severity: 'success',
      summary: 'Sucesso',
      detail: message
    };
  }
  static onErrorMessage(message) {
    return {
      severity: 'error',
      summary: 'Erro',
      detail: message
    };
  }
  static onInfoMessage(message) {
    return {
      severity: 'info',
      summary: 'Informação',
      detail: message
    };
  }
  static onWarningMessage(message) {
    return {
      severity: 'warn',
      summary: 'Atenção',
      detail: message
    };
  }
}
